package com.accolite.demo;

// TODO: Auto-generated Javadoc
/**
 * The Class Employee.
 */
public class Employee {
	
	/** The employee id. */
	private int employeeId;
	
	/** The employee name. */
	private String employeeName;
	
	/** The employee DOB. */
	private String employeeDOB;
	
	/** The employee email. */
	private String employeeEmail;
	
	/** The employee mobile number. */
	private String employeeMobileNumber;

	/**
	 * Gets the employee id.
	 *
	 * @return the employee id
	 */
	public int getEmployeeId() {
		return employeeId;
	}

	/**
	 * Sets the employee id.
	 *
	 * @param employeeId the new employee id
	 */
	public void setEmployeeId(int employeeId) {
		this.employeeId = employeeId;
	}

	/**
	 * Gets the employee name.
	 *
	 * @return the employee name
	 */
	public String getEmployeeName() {
		return employeeName;
	}

	/**
	 * Sets the employee name.
	 *
	 * @param employeeName the new employee name
	 */
	public void setEmployeeName(String employeeName) {
		this.employeeName = employeeName;
	}

	/**
	 * Gets the employee DOB.
	 *
	 * @return the employee DOB
	 */
	public String getEmployeeDOB() {
		return employeeDOB;
	}

	/**
	 * Sets the employee DOB.
	 *
	 * @param employeeDOB the new employee DOB
	 */
	public void setEmployeeDOB(String employeeDOB) {
		this.employeeDOB = employeeDOB;
	}

	/**
	 * Gets the employee email.
	 *
	 * @return the employee email
	 */
	public String getEmployeeEmail() {
		return employeeEmail;
	}

	/**
	 * Sets the employee email.
	 *
	 * @param employeeEmail the new employee email
	 */
	public void setEmployeeEmail(String employeeEmail) {
		this.employeeEmail = employeeEmail;
	}

	/**
	 * Gets the employee mobile number.
	 *
	 * @return the employee mobile number
	 */
	public String getEmployeeMobileNumber() {
		return employeeMobileNumber;
	}

	/**
	 * Sets the employee mobile number.
	 *
	 * @param employeeMobileNumber the new employee mobile number
	 */
	public void setEmployeeMobileNumber(String employeeMobileNumber) {
		this.employeeMobileNumber = employeeMobileNumber;
	}

}
